### **2. Socket.io Real-Time Server**

```typescript name=server/socket-server.ts
import http from 'http';
import express from 'express';
import { Server as SocketIOServer } from 'socket.io';
import { prisma } from '../lib/prisma';

const app = express();
const server = http.createServer(app);
const io = new SocketIOServer(server, {
  cors: {
    origin: process.env.NEXT_PUBLIC_FRONTEND_URL || 'http://localhost:3000',
    credentials: true,
  },
});

// =================== RIDE EVENTS ===================
io.on('connection', (socket) => {
  console.log(`[Socket] User connected: ${socket.id}`);

  // User joins their personal room
  socket.on('joinUserRoom', (userId: string) => {
    socket.join(`user:${userId}`);
    console.log(`[Ride] User ${userId} joined personal room`);
  });

  // User requests a ride
  socket.on('requestRide', async (data: {
    userId: string;
    pickupLat: number;
    pickupLng: number;
    dropoffLat: number;
    dropoffLng: number;
    pickupAddress: string;
    dropoffAddress: string;
  }) => {
    try {
      const ride = await prisma.ride.create({
        data: {
          userId: data.userId,
          pickupLat: data.pickupLat,
          pickupLng: data.pickupLng,
          dropoffLat: data.dropoffLat,
          dropoffLng: data.dropoffLng,
          pickupAddress: data.pickupAddress,
          dropoffAddress: data.dropoffAddress,
          status: 'requested',
          fare: 1000, // Calculate based on distance
        },
      });

      // Notify all drivers
      io.emit('rideAvailable', ride);
      console.log(`[Ride] Ride request created: ${ride.id}`);
    } catch (error) {
      console.error('[Ride] Error creating ride:', error);
    }
  });

  // Driver accepts a ride
  socket.on('acceptRide', async (data: { rideId: string; driverId: string }) => {
    try {
      const ride = await prisma.ride.update({
        where: { id: data.rideId },
        data: { driverId: data.driverId, status: 'accepted' },
      });
      io.emit('rideAccepted', ride);
      console.log(`[Ride] Ride ${data.rideId} accepted by driver ${data.driverId}`);
    } catch (error) {
      console.error('[Ride] Error accepting ride:', error);
    }
  });

  // Driver updates location
  socket.on('updateDriverLocation', (data: {
    driverId: string;
    rideId: string;
    lat: number;
    lng: number;
  }) => {
    io.emit('driverLocationUpdate', data);
  });

  // =================== FOOD DELIVERY EVENTS ===================
  socket.on('orderFood', async (data: {
    userId: string;
    restaurantId: string;
    items: any[];
    totalAmount: number;
    deliveryAddress: string;
  }) => {
    try {
      const order = await prisma.order.create({
        data: {
          userId: data.userId,
          restaurantId: data.restaurantId,
          items: data.items,
          totalAmount: data.totalAmount,
          deliveryFee: 200,
          tax: 0,
          finalAmount: data.totalAmount + 200,
          deliveryAddress: data.deliveryAddress,
          status: 'pending',
        },
      });
      io.emit('foodOrderCreated', order);
      console.log(`[Food] Order created: ${order.id}`);
    } catch (error) {
      console.error('[Food] Error creating order:', error);
    }
  });

  socket.on('courierAcceptDelivery', async (data: {
    orderId: string;
    courierId: string;
  }) => {
    try {
      const delivery = await prisma.delivery.create({
        data: {
          orderId: data.orderId,
          courierId: data.courierId,
          pickupLat: 0,
          pickupLng: 0,
          dropoffLat: 0,
          dropoffLng: 0,
          fare: 200,
          status: 'accepted',
        },
      });
      io.emit('deliveryAccepted', delivery);
      console.log(`[Delivery] Delivery ${delivery.id} accepted`);
    } catch (error) {
      console.error('[Delivery] Error accepting delivery:', error);
    }
  });

  socket.on('updateCourierLocation', (data: {
    courierId: string;
    deliveryId: string;
    lat: number;
    lng: number;
  }) => {
    io.emit('courierLocationUpdate', data);
  });

  // =================== GAME EVENTS ===================
  socket.on('joinGameTournament', async (data: {
    userId: string;
    tournamentId: string;
    betAmount: number;
  }) => {
    try {
      // Create a match
      const tournament = await prisma.tournament.findUnique({
        where: { id: data.tournamentId },
      });

      if (!tournament) {
        socket.emit('error', 'Tournament not found');
        return;
      }

      // Deduct bet from wallet
      const wallet = await prisma.wallet.update({
        where: { userId: data.userId },
        data: { locked: { increment: data.betAmount } },
      });

      io.emit('playerJoinedGame', {
        userId: data.userId,
        tournamentId: data.tournamentId,
        betAmount: data.betAmount,
      });

      console.log(`[Game] Player ${data.userId} joined tournament ${data.tournamentId}`);
    } catch (error) {
      console.error('[Game] Error joining tournament:', error);
    }
  });

  socket.on('gameResult', async (data: {
    matchId: string;
    winnerId: string;
    betAmount: number;
  }) => {
    try {
      const match = await prisma.match.update({
        where: { id: data.matchId },
        data: { winnerId: data.winnerId, status: 'completed' },
      });

      // Credit winner's wallet
      const winnings = data.betAmount * 2;
      await prisma.wallet.update({
        where: { userId: data.winnerId },
        data: { balance: { increment: winnings }, locked: { decrement: data.betAmount } },
      });

      // Debit loser's wallet
      const loserIds = [match.player1Id, match.player2Id].filter(
        (id) => id !== data.winnerId
      );
      for (const loserId of loserIds) {
        await prisma.wallet.update({
          where: { userId: loserId },
          data: { locked: { decrement: data.betAmount } },
        });
      }

      io.emit('gameCompleted', match);
      console.log(`[Game] Match ${data.matchId} completed. Winner: ${data.winnerId}`);
    } catch (error) {
      console.error('[Game] Error processing game result:', error);
    }
  });

  // =================== WALLET EVENTS ===================
  socket.on('walletTransaction', async (data: {
    userId: string;
    type: string;
    amount: number;
  }) => {
    try {
      const transaction = await prisma.transaction.create({
        data: {
          walletId: (await prisma.wallet.findUnique({ where: { userId: data.userId } }))!
            .id,
          type: data.type,
          amount: data.amount,
          status: 'completed',
        },
      });
      io.to(`user:${data.userId}`).emit('transactionProcessed', transaction);
      console.log(`[Wallet] Transaction: ${data.type} - ${data.amount} for user ${data.userId}`);
    } catch (error) {
      console.error('[Wallet] Error processing transaction:', error);
    }
  });

  // =================== LIVE STATS ===================
  socket.on('requestLiveStats', async () => {
    try {
      const activeRides = await prisma.ride.count({
        where: { status: { in: ['requested', 'accepted', 'in_transit'] } },
      });
      const activeOrders = await prisma.order.count({
        where: { status: { in: ['pending', 'confirmed', 'on_delivery'] } },
      });
      const activeTournaments = await prisma.tournament.count({
        where: { status: 'active' },
      });

      const stats = {
        activeRides,
        activeOrders,
        activeTournaments,
        timestamp: new Date(),
      };

      socket.emit('liveStatsUpdate', stats);
    } catch (error) {
      console.error('[Stats] Error fetching stats:', error);
    }
  });

  // =================== ADMIN DASHBOARD ===================
  socket.on('adminJoinDashboard', (adminId: string) => {
    socket.join('admin-dashboard');
    console.log(`[Admin] Admin ${adminId} joined dashboard`);
  });

  socket.on('disconnect', () => {
    console.log(`[Socket] User disconnected: ${socket.id}`);
  });
});

const PORT = process.env.SOCKET_PORT || 3001;
server.listen(PORT, () => {
  console.log(`üöÄ Socket.io server running on port ${PORT}`);
});
```

