### **5. Updated Prisma Seed Script**

```typescript name=prisma/seed.ts
import { PrismaClient } from '@prisma/client';
import bcrypt from 'bcrypt';

const prisma = new PrismaClient();

async function main() {
  console.log('üå± Starting seed...');

  // Clear existing data
  await prisma.match.deleteMany();
  await prisma.tournament.deleteMany();
  await prisma.delivery.deleteMany();
  await prisma.order.deleteMany();
  await prisma.restaurant.deleteMany();
  await prisma.ride.deleteMany();
  await prisma.transaction.deleteMany();
  await prisma.wallet.deleteMany();
  await prisma.refreshToken.deleteMany();
  await prisma.profile.deleteMany();
  await prisma.homePhoto.deleteMany();
  await prisma.user.deleteMany();

  // Create users
  const users = [];
  const userEmails = [
    { email: 'user1@example.com', name: 'John Doe' },
    { email: 'user2@example.com', name: 'Jane Smith' },
    { email: 'driver1@example.com', name: 'Mike Johnson', isDriver: true },
    { email: 'driver2@example.com', name: 'Sarah Lee', isDriver: true },
    { email: 'courier1@example.com', name: 'Alex Brown', isCourier: true },
    { email: 'restaurant1@example.com', name: 'Pizza Palace', isRestaurant: true },
    { email: 'admin@example.com', name: 'Admin User', isAdmin: true },
  ];

  for (const userData of userEmails) {
    const hashedPassword = await bcrypt.hash('password123', 10);
    const user = await prisma.user.create({
      data: {
        email: userData.email,
        name: userData.name,
        password: hashedPassword,
        isDriver: userData.isDriver || false,
        isCourier: userData.isCourier || false,
        isRestaurant: userData.isRestaurant || false,
        isAdmin: userData.isAdmin || false,
        avatar: `https://api.dicebear.com/7.x/avataaars/svg?seed=${userData.email}`,
        profile: {
          create: {
            displayName: userData.name,
            avatar: `https://api.dicebear.com/7.x/avataaars/svg?seed=${userData.email}`,
          },
        },
        wallet: {
          create: {
            balance: 50000, // Initial balance in NGN
          },
        },
      },
    });
    users.push(user);
    console.log(`‚úÖ Created user: ${user.email}`);
  }

  // Create restaurants
  const restaurants = [
    {
      name: 'Pizza Palace',
      description: 'Authentic Italian pizzas',
      location: 'Lagos, Nigeria',
      latitude: 6.5244,
      longitude: 3.3792,
      phone: '+234701234567',
      deliveryTime: 30,
      isFastestDelivery: true,
      menu: JSON.stringify([
        { id: 1, name: 'Margherita', price: 3500, image: 'https://via.placeholder.com/300' },
        { id: 2, name: 'Pepperoni', price: 4000, image: 'https://via.placeholder.com/300' },
        { id: 3, name: 'Vegetarian', price: 3000, image: 'https://via.placeholder.com/300' },
      ]),
    },
    {
      name: 'Chicken Delight',
      description: 'Fried chicken specialty',
      location: 'Lagos, Nigeria',
      latitude: 6.5150,
      longitude: 3.3600,
      phone: '+234701234568',
      deliveryTime: 25,
      isFastestDelivery: false,
      menu: JSON.stringify([
        { id: 1, name: 'Crispy Chicken', price: 2500, image: 'https://via.placeholder.com/300' },
        { id: 2, name: 'Chicken Combo', price: 5000, image: 'https://via.placeholder.com/300' },
        { id: 3, name: 'Spicy Wings', price: 2000, image: 'https://via.placeholder.com/300' },
      ]),
    },
  ];

  for (const restaurantData of restaurants) {
    const restaurant = await prisma.restaurant.create({
      data: restaurantData,
    });
    console.log(`‚úÖ Created restaurant: ${restaurant.name}`);
  }

  // Create tournaments
  const tournaments = [
    {
