'use client';

import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { useSocket } from '@/lib/useSocket';
import axios from 'axios';

export default function RidesPage() {
  const { socket, isConnected } = useSocket();
  const [rides, setRides] = useState<any[]>([]);
  const [pickup, setPickup] = useState('');
  const [dropoff, setDropoff] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (isConnected && socket) {
      socket.on('rideAccepted', (ride) => {
        setRides((prev) => [...prev, ride]);
      });

      socket.on('rideAvailable', (ride) => {
        console.log('New ride available:', ride);
      });

      return () => {
        socket.off('rideAccepted');
        socket.off('rideAvailable');
      };
    }
  }, [socket, isConnected]);

  const handleRequestRide = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);

    try {
      const response = await axios.post('/api/rides/request', {
        pickup,
        dropoff,
      });

      socket?.emit('requestRide', response.data);
      setPickup('');
      setDropoff('');
    } catch (error) {
      console.error('Error requesting ride:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-blue-900 to-slate-900 p-4 md:p-8">
      <div className="max-w-6xl mx-auto">
        {/* Header */}
        <motion.div
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          className="mb-8"
        >
          <h1 className="text-4xl md:text-5xl font-black bg-gradient-to-r from-cyan-400 to-blue-400 bg-clip-text text-transparent mb-2">
            Request a Ride
          </h1>
          <p className="text-gray-400">Get from point A to B safely and affordably</p>
        </motion.div>

        {/* Connection Status */}
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          className="mb-6 p-4 rounded-lg bg-white/5 border border-cyan-400/30 flex items-center gap-3"
        >
          <div
            className={`w-3 h-3 rounded-full ${
              isConnected ? 'bg-green-500 animate-pulse' : 'bg-red-500'
            }`}
          />
          <span className="text-sm text-gray-300">
            {isConnected ? 'Connected to real-time service' : 'Connecting...'}
          </span>
        </motion.div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Request Form */}
          <motion.div
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            className="lg:col-span-1"
          >
            <form
              onSubmit={handleRequestRide}
              className="bg-white/5 backdrop-blur-md border border-cyan-400/30 rounded-2xl p-6 space-y-4"
            >
              <div>
                <label className="block text-sm font-semibold text-gray-300 mb-2">
                  Pickup Location
                </label>
                <input
                  type="text"
                  value={pickup}
                  onChange={(e) => setPickup(e.target.value)}
                  placeholder="Enter pickup address"
                  className="w-full bg-white/10 border border-gray-600 rounded-lg p-3 text-white placeholder-gray-500 focus:outline-none focus:border-cyan-400"
                />
              </div>

              <div>
                <label className="block text-sm font-semibold text-gray-300 mb-2">
                  Dropoff Location
                </label>
                <input
                  type="text"
                  value={dropoff}
                  onChange={(e) => setDropoff(e.target.value)}
                  placeholder="Enter dropoff address"
                  className="w-full bg-white/10 border border-gray-600 rounded-lg p-3 text-white placeholder-gray-500 focus:outline-none focus:border-cyan-400"
                />
              </div>

              <motion.button
                whileHover={{ scale: 1.02 }}
                whileTap={{ scale: 0.98 }}
                type="submit"
                disabled={loading || !pickup || !dropoff}
                className="w-full bg-gradient-to-r from-cyan-500 to-blue-500 text-white font-bold py-3 rounded-lg hover:shadow-lg hover:shadow-cyan-500/50 disabled:opacity-50 transition-all"
              >
                {loading ? 'Requesting...' : 'Request Ride'}
              </motion.button>
            </form>
          </motion.div>

          {/* Active Rides */}
          <motion.div
            initial={{ opacity: 0, x: 20 }}
            animate={{ opacity: 1, x: 0 }}
            className="lg:col-span-2"
          >
            <h2 className="text-2xl font-bold text-white mb-4">Active Rides</h2>
            {rides.length === 0 ? (
              <div className="bg-white/5 backdrop-blur-md border border-cyan-400/30 rounded-2xl p-8 text-center">
                <p className="text-gray-400">No active rides yet</p>
              </div>
            ) : (
              <div className="space-y-4">
                {rides.map((ride, index) => (
                  <motion.div
                    key={ride.id}
                    initial={{ opacity: 0, y: 10 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: index * 0.1 }}
                    className="bg-white/5 backdrop-blur-md border border-cyan-400/30 rounded-xl p-4"
                  >
                    <div className="flex justify-between items-start mb-3">
                      <div>
                        <p className="text-sm font-semibold text-cyan-400">{ride.pickupAddress}</p>
                        <p className="text-sm text-gray-400">{ride.dropoffAddress}</p>
                      </div>
                      <span
