        totalUsers: Math.floor(Math.random() * 50000) + 10000,
      });
    };

    const interval = setInterval(updateStats, 3000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="relative w-full h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 overflow-hidden pt-20">
      {/* 3D Canvas Background */}
      <div className="absolute inset-0 top-20">
        <Scene3D />
      </div>

      {/* Overlay Content */}
      <div className="absolute inset-0 flex flex-col items-center justify-center z-10 top-20">
        <motion.div
          initial={{ opacity: 0, y: -50 }}
