import React, { useEffect, useRef } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { OrbitControls, PerspectiveCamera } from '@react-three/drei';
import * as THREE from 'three';
import { motion } from 'framer-motion';

// 3D Animated Particles
const Particles = () => {
  const points = useRef<THREE.Points>(null);
  const particleCount = 1000;

  useEffect(() => {
    const geometry = new THREE.BufferGeometry();
    const positions = new Float32Array(particleCount * 3);

    for (let i = 0; i < particleCount * 3; i += 3) {
      positions[i] = (Math.random() - 0.5) * 100;
      positions[i + 1] = (Math.random() - 0.5) * 100;
      positions[i + 2] = (Math.random() - 0.5) * 100;
    }

    geometry.setAttribute('position', new THREE.BufferAttribute(positions, 3));
    return () => geometry.dispose();
  }, []);

  useFrame(() => {
    if (points.current) {
      points.current.rotation.x += 0.0001;
      points.current.rotation.y += 0.0002;
    }
  });

  return (
    <points ref={points}>
      <bufferGeometry />
      <pointsMaterial size={0.2} sizeAttenuation color="#00ff88" />
    </points>
  );
};

// 3D Floating Cube
const FloatingCube = ({ position }: { position: [number, number, number] }) => {
  const mesh = useRef<THREE.Mesh>(null);

  useFrame(() => {
    if (mesh.current) {
      mesh.current.rotation.x += 0.005;
      mesh.current.rotation.y += 0.008;
      mesh.current.position.y += Math.sin(Date.now() * 0.001) * 0.01;
    }
  });

  return (
    <mesh ref={mesh} position={position}>
      <boxGeometry args={[1.5, 1.5, 1.5]} />
      <meshStandardMaterial color="#00ff88" emissive="#00ff88" emissiveIntensity={0.5} />
    </mesh>
  );
};

// 3D Scene
const Scene3D = () => {
  return (
    <Canvas className="w-full h-screen">
      <PerspectiveCamera position={[0, 0, 30]} fov={75} />
      <ambientLight intensity={0.6} />
      <pointLight position={[10, 10, 10]} intensity={1} />
      <Particles />
      <FloatingCube position={[-5, 0, 0]} />
      <FloatingCube position={[5, 0, 0]} />
      <FloatingCube position={[0, 5, 0]} />
      <OrbitControls autoRotate autoRotateSpeed={2} />
    </Canvas>
  );
};

export const Home3DHero: React.FC = () => {
  const [stats, setStats] = React.useState({
    activeRides: 245,
    activeFoods: 187,
    activeGames: 92,
    totalUsers: 45230,
  });

  useEffect(() => {
    const updateStats = () => {
      setStats({
        activeRides: Math.floor(Math.random() * 500) + 100,
        activeFoods: Math.floor(Math.random() * 300) + 50,
        activeGames: Math.floor(Math.random() * 200) + 30,
        totalUsers: Math.floor(Math.random() * 50000) + 10000,
      });
    };

    const interval = setInterval(updateStats, 3000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="relative w-full h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 overflow-hidden pt-20">
      {/* 3D Canvas Background */}
      <div className="absolute inset-0 top-20">
        <Scene3D />
      </div>

      {/* Overlay Content */}
      <div className="absolute inset-0 flex flex-col items-center justify-center z-10 top-20">
        <motion.div
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1 }}
          className="text-center mb-16"
        >
          <h1 className="text-6xl md:text-8xl font-black bg-gradient-to-r from-cyan-400 via-purple-400 to-pink-400 bg-clip-text text-transparent mb-4">
            J'S ARCHED
          </h1>
          <p className="text-xl md:text-2xl text-gray-300 font-light tracking-widest">
            The Future of Super Apps
          </p>
        </motion.div>

        {/* Live Stats Grid */}
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-16 max-w-4xl mx-auto px-4">
