'use client';

import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { useSocket } from '@/lib/useSocket';
import axios from 'axios';
import { FaUsers, FaMoneyBillWave, FaCar, FaGamepad, FaTrophy } from 'react-icons/fa';

interface AdminStats {
  totalUsers: number;
  totalRevenue: number;
  activeRides: number;
  activeOrders: number;
  activeTournaments: number;
}

export default function AdminPage() {
  const { socket, isConnected } = useSocket();
  const [stats, setStats] = useState<AdminStats>({
    totalUsers: 0,
    totalRevenue: 0,
    activeRides: 0,
    activeOrders: 0,
    activeTournaments: 0,
  });
  const [liveData, setLiveData] = useState<any[]>([]);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const response = await axios.get('/api/admin/stats');
        setStats(response.data);
      } catch (error) {
        console.error('Error fetching stats:', error);
      }
    };

    fetchStats();
    const interval = setInterval(fetchStats, 5000);
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    if (isConnected && socket) {
      socket.emit('adminJoinDashboard', 'admin-user-id');

      socket.on('liveStatsUpdate', (data) => {
        setStats((prev) => ({
          ...prev,
          ...data,
        }));
        setLiveData((prev) => [{ timestamp: new Date(), ...data }, ...prev.slice(0, 9)]);
      });

      return () => {
        socket.off('liveStatsUpdate');
      };
    }
  }, [socket, isConnected]);

  const metrics = [
    { icon: FaUsers, label: 'Total Users', value: stats.totalUsers, color: 'bg-blue-500' },
    { icon: FaMoneyBillWave, label: 'Total Revenue', value: `‚Ç¶${stats.totalRevenue.toLocaleString()}`, color: 'bg-green-500' },
    { icon: FaCar, label: 'Active Rides', value: stats.activeRides, color: 'bg-cyan-500' },
