'use client';

import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { FaChevronLeft, FaChevronRight, FaStar } from 'react-icons/fa';

interface Client {
  id: number;
  name: string;
  image: string;
  feedback: string;
  rating: number;
  service: string;
}

const clients: Client[] = [
  {
    id: 1,
    name: 'Ahmed Hassan',
    image: 'https://api.dicebear.com/7.x/avataaars/svg?seed=ahmed',
    feedback: 'Amazing ride service! Driver was professional and on time.',
    rating: 5,
    service: 'Rides',
  },
  {
    id: 2,
    name: 'Fatima Al-Rashid',
    image: 'https://api.dicebear.com/7.x/avataaars/svg?seed=fatima',
    feedback: 'Food arrived fresh and hot. Best delivery in town!',
    rating: 5,
    service: 'Food Delivery',
  },
  {
    id: 3,
    name: 'Mohammed Khan',
    image: 'https://api.dicebear.com/7.x/avataaars/svg?seed=mohammed',
    feedback: 'Won big at Ludo tournament. Platform is fair and transparent.',
    rating: 5,
    service: 'Games',
  },
  {
    id: 4,
    name: 'Noor Ibrahim',
    image: 'https://api.dicebear.com/7.x/avataaars/svg?seed=noor',
    feedback: 'Quick package delivery. Courier was friendly and careful.',
    rating: 5,
    service: 'Dispatch',
  },
  {
    id: 5,
    name: 'Omar Al-Zahra',
    image: 'https://api.dicebear.com/7.x/avataaars/svg?seed=omar',
    feedback: 'Wallet system is so secure. Easy deposits and withdrawals.',
    rating: 5,
    service: 'Wallet',
  },
];

export const HappyClientsCarousel: React.FC = () => {
  const [current, setCurrent] = useState(0);
  const [autoPlay, setAutoPlay] = useState(true);

  useEffect(() => {
    if (!autoPlay) return;
    const timer = setInterval(() => {
      setCurrent((prev) => (prev + 1) % clients.length);
    }, 5000);
    return () => clearInterval(timer);
  }, [autoPlay]);

  const next = () => {
    setCurrent((prev) => (prev + 1) % clients.length);
    setAutoPlay(false);
  };

  const prev = () => {
    setCurrent((prev) => (prev - 1 + clients.length) % clients.length);
    setAutoPlay(false);
  };

  return (
    <section className="py-20 px-4 bg-gradient-to-b from-purple-900 to-slate-900">
      <div className="max-w-5xl mx-auto">
        <motion.h2
          initial={{ opacity: 0 }}
          whileInView={{ opacity: 1 }}
          className="text-4xl md:text-5xl font-black text-center mb-16 bg-gradient-to-r from-cyan-400 to-purple-400 bg-clip-text text-transparent"
        >
          What Our Happy Clients Say
        </motion.h2>

        <div className="relative">
          <AnimatePresence mode="wait">
            <motion.div
              key={current}
              initial={{ opacity: 0, x: 100 }}
              animate={{ opacity: 1, x: 0 }}
              exit={{ opacity: 0, x: -100 }}
              transition={{ duration: 0.5 }}
              className="glass rounded-2xl p-8 md:p-12 text-center"
            >
              <img
                src={clients[current].image}
                alt={clients[current].name}
                className="w-24 h-24 rounded-full mx-auto mb-6 border-4 border-cyan-400"
              />
              <h3 className="text-2xl font-bold text-white mb-2">{clients[current].name}</h3>
              <p className="text-cyan-400 font-semibold mb-4">{clients[current].service}</p>
              <div className="flex justify-center gap-1 mb-4">
                {Array(clients[current].rating)
                  .fill(0)
                  .map((_, i) => (
                    <FaStar key={i} className="text-yellow-400" />
                  ))}
              </div>
              <p className="text-gray-300 text-lg italic">"{clients[current].feedback}"</p>
            </motion.div>
          </AnimatePresence>

          {/* Navigation Buttons */}
          <motion.button
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.95 }}
            onClick={prev}
            className="absolute left-0 top-1/2 -translate-y-1/2 -translate-x-16 md:-translate-x-20 bg-cyan-500 hover:bg-cyan-600 text-white p-3 rounded-full transition-all"
          >
            <FaChevronLeft />
          </motion.button>
          <motion.button
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.95 }}
            onClick={next}
            className="absolute right-0 top-1/2 -translate-y-1/2 translate-x-16 md:translate-x-20 bg-cyan-500 hover:bg-cyan-600 text-white p-3 rounded-full transition-all"
          >
            <FaChevronRight />
          </motion.button>

          {/* Dots Indicator */}
          <div className="flex justify-center gap-2 mt-8">
            {clients.map((_, index) => (
              <motion.button
                key={index}
                onClick={() => {
                  setCurrent(index);
                  setAutoPlay(false);
                }}
                className={`w-3 h-3 rounded-full transition-all ${
                  index === current ? 'bg-cyan-400 w-8' : 'bg-gray-600'
                }`}
              />
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

